insert into Nano_suits (NS_id, NS_title_fr, NS_title_en, NS_text_1_fr,NS_text_1_en, NS_text_2_fr, NS_text_2_en, NS_Where_title_fr, NS_Where_title_en, NS_Where_text_fr, NS_Where_text_en, NS_picture, NS_Stars, NS_id_fk)
values
    (1, "Ailes écarlates", "Crimson Wings", "Permet de modifier l'apparence d'EVE.", "Changes the appearance of EVE.", "Façonnée comme de délicates ailes écarlates, cette robe a été conçue pour briller dans les moments les plus heureux.", "A dress to shine in your most beautiful moments, crafted as delicately as crimson wings.", "Où la trouver ?", "Where to find it ?", "Inconnu", "Unknown", "/images/nano_suits/unavailable.webp", "/icons/star_fill_blue.webp", 1),
    (2, "Alice ensoleillée", "Midsummer Alice", "Permet de modifier l'apparence d'EVE", "Changes the appearance of EVE.", "Découvrez la collection de maillots de bain ensoleillée de Tetrastar C&T: partez en vacances au Pays des merveilles !", "Introducing Tetrastar C&T's Midsummer Collection swimwear. Step into Wonderland's vacation spot and make it your own.", "Où la trouver ?", "Where to find it ?", "Dans la boutique Tetrastar C&T", "Unknown", "/images/nano_suits/Alice_ensoleillee.webp", "/icons/star_fill_blue.webp", 1),
    (3, "Aventurière des terres désolée", "Wasteland Adventurer", "Permet de modifier l'apparence d'EVE", "Changes the appearance of EVE.", "Personne ne peut vous arrêter, Traversez les zones hostiles et franchissez les obstacles avec la tenue Aventurière. Bien entendu, n'allez pas chercher de poux dans la tête des Naytibas.", "Nobody can stop you. Advance beyond tough areas and obstacles together with the Adventurer. Of course, don't try to mess with the Naytibas.", "Où la trouver ?", "Where to find it ?", "Dans la boutique Tetrastar C&T", "Unknown", "/images/nano_suits/Aventuriere_des_terres_desolee.webp", "/icons/star_fill_blue.webp", 1),
    (4, "Bikini de plongée Vague", "Wave Diver Bikini", "Permet de modifier l'apparence d'EVE", "Changes the appearance of EVE.", "Suite à la disparition mystérieuse de « Galaxy » Alan de Tetrastar C&T, « Kitsune » Maria a repris sa position vacante afin de préserver les valeurs de l'entreprise. Maria a ensuite présenté « Vague », une nouvelle marque respectueuse de l'environnement axée sur la durabilité, qui n'utilise que des matériaux polymères provenant de filets de pêche recyclés. Elle a alors lancé sa collection de maillots de bain, marquant le début de l'ascension inarrêtable de sa carrière.", "After the mysterious disappearance of 'Galaxy' Alan from Tetrastar C&T, 'Kitsune' Maria assumed his vacant position to uphold the corporate values. Maria introduced 'Wave', a new environmentally friendly brand centered around 'Sustainability', utilizing only polymer materials sourced from recycled fishnets. She subsequently debuted her own swimwear collection, marking the beginning of her career's unstoppable ascent.", "Où la trouver ?", "Where to find it ?", "Dans la boutique Tetrastar C&T", "Unknown", "/images/nano_suits/Bikini_de_plongee_Vague.webp", "/icons/star_fill_blue.webp", 1),
    (5, "Bondage cybernétique", "Cybernetic Bondage", "Permet de modifier l'apparence d'EVE", "Changes the appearance of EVE.", "Cette tenue de la collection « Sens exotique » a été conçue par le styliste phare de Tetrastar C&T : « Galaxy » Alan. D'après la rumeur, les fibres, la doublure et les nœuds sont constitués de nerfs quantiques qui créent un lien avec le corps et permettent une totale liberté de mouvement.", "This is the 'Exotic Sense' collection outfit designed by Tetrastar C&T's lead designer 'Galaxy' Alan. It is said that Quantum Nerves are inserted into the linings, strings, and knots, so that it moves freely through a link with the body.", "Où la trouver ?", "Where to find it ?", "Dans la boutique Tetrastar C&T", "Unknown", "/images/nano_suits/Bondage_cybernetique.webp", "/icons/star_fill_blue.webp", 1),
    (6, "Chaînon manquant", "Missing Link", "Permet de modifier l'apparence d'EVE.", "Changes the appearance of EVE.", "Cette tenue mystérieuse dont on ne connaît ni la matière, ni le procédé de fabrication, reste une énigme aux yeux de tous. La seule certitude à son sujet, c'est qu'elle a été conçue grâce à une technologie qui dépasse nos connaissances actuelles.", " This mysterious garment remains an enigma, with its composition and construction methods entirely unknown. The only certainty is that it was created using technology far beyond our current understanding.", "où la trouver ?", "Where to find it ?", "inconnu", "Unknown", "/images/nano_suits/unavailable.webp", "/icons/star_fill_blue.webp", 1),
    (7, "Chaperon rouge ensoleillé", "Midsummer Redhood", "Permet de modifier l'apparence d'EVE", "Changes the appearance of EVE.", "Découvrez la collection de maillots de bain ensoleillée de Tetrastar C&T: même le grand méchant loup ne pourra pas perturber les vacances du Chaperon rouge !", "Introducing Tetrastar C&T's Midsummer Collection swimwear, where even the big bad wolf can't bother Red Hood's summer escape.", "Où la trouver ?", "Where to find it ?", "Dans la boutique Tetrastar C&T", "Unknown", "/images/nano_suits/Chaperon_rouge_ensoleille.webp", "/icons/star_fill_blue.webp", 1),
    (8, "Combinaison d'exploration d'Orca", "Orca Exploration Suit", "Permet de modifier l'apparence d'EVE", "Changes the appearance of EVE.", "La Combinaison d'exploration d'Orca est le fruit de la collaboration entre Tetrastar C&T et Orca Aerospace, Ce haut de compression est conçu pour être porté en dessous d'une combinaison spatiale. N'effectuez jamais de sortie dans le vide intersidéral si vous ne portez rien d'autre !", "Orca Exploration Suit, a collaboration suit between Tetrastar C&T and Orca Aerospace Company, is a rash guard made for spacewalks. It's designed to be worn beneath your spacesuit. Of course, you should never go into space wearing only this!", "Où la trouver ?", "Where to find it ?", "Dans la boutique Tetrastar C&T", "Unknown", "/images/nano_suits/Combinaison_d-exploration_d-Orca.webp", "/icons/star_fill_blue.webp", 1),
    (9, "Combinaison de guerre", "War Suit", "Permet de modifier l'apparence d'EVE.", "Changes the appearance of EVE.", "Cet incroyable ensemble est le fruit de la collaboration entre Tetrastar C&T et le jeu vidéo War Maiden. Cependant, le jeu a sombré dans l'oubli et cette tenue en est le dernier vestige.","This extraordinary ensemble is the fruit of a collaboration between Tetrastar C&T and the video game 'War Maiden'. But as the game itself faded into obscurity, this outfit is all that remains.", "Où la trouver ?", "Where to find it ?", "Dans la boutique Tetrastar C&T", "Unknown", "/images/nano_suits/unavailable.webp", "/icons/star_fill_blue.webp", 1),
    (10, "Combinaison de plongée planétaire (2)", "Planet Diving Suit (2nd)", "Permet de modifier l'apparence d'EVE", "Changes the appearance of EVE.", "« Enfant né par une nuit étoilée. Accomplis ta mission. Garde à jamais la tête haute. Notre futur est entre tes mains,»", "'Children born on a starry night. Go forth and fulfil your mission. Be proud, forever and always. Our future is in your hands.'", "Où la trouver ?", "Where to find it ?", "Dans la boutique Tetrastar C&T", "Unknown", "/images/nano_suits/Combinaison_de_plongee_planetaire_(2).webp", "/icons/star_fill_blue.webp", 1),
    (11, "Combinaison de plongée planétaire (2) V2", "Planet Diving Suit (2nd) V2", "Permet de modifier l'apparence d'EVE", "Changes the appearance of EVE.", "« Enfant né par une nuit étoilée. Accomplis ta mission. Garde à jamais la tête haute. Notre futur est entre tes mains,»", "'Children born on a starry night. Go forth and fulfil your mission. Be proud, forever and always. Our future is in your hands.'", "Où la trouver ?", "Where to find it ?", "Dans la boutique Tetrastar C&T", "Unknown", "/images/nano_suits/Combinaison_de_plongee_planetaire_(2)_V2.webp", "/icons/star_fill_blue.webp", 1),
    (12, "Combinaison de plongée planétaire (3)", "Planet Diving Suit (3rd)", "Permet de modifier l'apparence d'EVE", "Changes the appearance of EVE.", "« Enfant né par une nuit étoilée. Accomplis ta mission. Garde à jamais la tête haute. Notre futur est entre tes mains,»", "'Children born on a starry night. Go forth and fulfil your mission. May you never forget the passion in your heart. Our future is in your hands.'", "Où la trouver ?", "Where to find it ?", "Dans la boutique Tetrastar C&T", "Unknown", "/images/nano_suits/Combinaison_de_plongee_planetaire_(3).webp", "/icons/star_fill_blue.webp", 1),
