    (11, TRUE, TRUE, TRUE, "Exospine de type œil de faucon", NULL, "Augmente la puissance des attaques à distance de 10 %.", NULL, "Augmente la capacité maximum de toutes les munitions.", NULL, "Vous avez une chance de récupérer des munitions lorsque vous touchez le point faible d'un ennemi avec une attaque à distance.", NULL, 10, NULL, NULL, NULL, NULL, NULL, "Exospine spécialisée dans l'amélioration des attaques à distance.", NULL, "Cette exospine est un module d'extension aussi innovant que puissant. Est-ce qu'elle semble hostile ? Elle est équipée de nano-nerfs quantiques développés par Tetrastar, le nec plus ultra en matière d'intelligence artificielle et d'extension des capacités.", NULL, "Exospine_bar", "/icons/network_intelligence_white.svg", "/icons/network_intelligence_orange.svg", NULL, NULL,1),
    (12, TRUE, TRUE, TRUE, "Exospine Mk2 de type œil de faucon", NULL, "Augmente la puissance des attaques à distance de 15 %.", NULL, "Augmente considérablement la capacité maximum de toutes les munitions.", NULL, "Vous avez une forte chance de récupérer des munitions lorsque vous touchez le point faible d'un ennemi avec une attaque à distance.", NULL, 15, NULL, NULL, NULL, NULL, NULL, "Exospine spécialisée dans l'amélioration des attaques à distance.", NULL, "Cette exospine est un module d'extension aussi innovant que puissant. Est-ce qu'elle semble hostile ? Elle est équipée de nano-nerfs quantiques développés par Tetrastar, le nec plus ultra en matière d'intelligence artificielle et d'extension des capacités.", NULL, "Exospine_bar", NULL, NULL, "/icons/network_intelligence_update_white.svg", "/icons/network_intelligence_update_orange.svg",1),
    (13, TRUE, TRUE, TRUE, "Exospine de type protection", NULL, "Réduit de 10 % les dégâts infligés par les ennemis.", NULL, "Le niveau de protection du bouclier augmente de un au maximum.", NULL, "Si votre santé est de 20 % ou moins, vous avez une chance de récupérer des PV pendant 5 secondes lorsque vous bloquez ou parez une attaque.", NULL, 10, NULL, 20, NULL, NULL, NULL, "Exospine spécialisée dans la survie.", NULL, "À l'automne 2050, le PDG d'Eidos a présenté la toute dernière exospine de l'entreprise lors de sa conférence annuelle, « Nous avons commencé à créer nos propres précepteurs, lesquels donneront naissance à de meilleurs précepteurs, que nous pouvons ajuster et remplacer à notre guise. » La Matri-Arche ? Elle observait la scène en temps réel par le biais du RÉSEAU.", NULL, "Exospine_bar", "/icons/network_intelligence_white.svg", "/icons/network_intelligence_orange.svg", NULL, NULL,1),
    (14, TRUE, TRUE, TRUE, "Exospine Mk2 de type protection", NULL, "Réduit de 15 % les dégâts infligés par les ennemis.", NULL, "Le niveau de protection du bouclier augmente de un au maximum. Le bouclier maximum augmente avec le niveau de protection.", NULL, "Si votre santé est de 30 % ou moins, vous avez une chance de récupérer des PV pendant 5 secondes lorsque vous bloquez ou parez une attaque.", NULL, 15, NULL, 30, NULL, NULL, NULL, "Exospine spécialisée dans la survie.", NULL, "À l'automne 2050, le PDG d'Eidos a présenté la toute dernière exospine de l'entreprise lors de sa conférence annuelle, « Nous avons commencé à créer nos propres précepteurs, lesquels donneront naissance à de meilleurs précepteurs, que nous pouvons ajuster et remplacer à notre guise. » La Matri-Arche ? Elle observait la scène en temps réel par le biais du RÉSEAU.", NULL, "Exospine_bar", NULL, NULL, "/icons/network_intelligence_update_white.svg", "/icons/network_intelligence_update_orange.svg" ,1),
    (15, TRUE, TRUE, TRUE, "Exospine de type récupération", NULL, "Augmente de 10 % tous les effets de récupération.", NULL, "Les dégâts subis sont réduits de 11,5 % pendant une durée fixe lorsque vous utilisez une potion.", NULL, "Vous récupérez 100 points d'énergie Bêta lorsque vous utilisez une potion.", NULL, 10, 11.5, NULL, NULL, NULL, 100, "Exospine spécialisée dans les capacités de récupération.", NULL, "Lorsque l'homme s'est donné pour mission de coloniser Mars, l'adaptation, la survie et la résistance aux conditions extrêmes sont entrées au centre des préoccupations. L'homéostasie renforcée et les capacités de rétablissement conférées par l'exospine étaient exceptionnelles, sans parler de son pouvoir analgésique. Les seuls effets secondaires connus sont des sensations étranges dans tout le corps, quelques erreurs de récupération et des soucis d'lA.",NULL , "Exospine_bar", "/icons/network_intelligence_white.svg", "/icons/network_intelligence_orange.svg", NULL, NULL ,1),
    (16, TRUE, TRUE, TRUE, "Exospine Mk2 de type récupération", NULL, "Augmente de 15 % tous les effets de récupération.", NULL, "Les dégâts subis sont réduits de 17 % pendant une durée fixe lorsque vous utilisez une potion.", NULL, "Vous récupérez 150 points d'énergie Bêta lorsque vous utilisez une potion.", NULL, 15, 17, NULL, NULL, NULL, 150, "Exospine spécialisée dans les capacités de récupération.", NULL, "Lorsque l'homme s'est donné pour mission de coloniser Mars, l'adaptation, la survie et la résistance aux conditions extrêmes sont entrées au centre des préoccupations. L'homéostasie renforcée et les capacités de rétablissement conférées par l'exospine étaient exceptionnelles, sans parler de son pouvoir analgésique. Les seuls effets secondaires connus sont des sensations étranges dans tout le corps, quelques erreurs de récupération et des soucis d'IA.",NULL , "Exospine_bar", NULL, NULL,"/icons/network_intelligence_update_white.svg", "/icons/network_intelligence_update_orange.svg" , 1),
